DROP TABLE IF EXISTS `ithink_blog_article`;
CREATE TABLE `ithink_blog_article` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `title` char(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '标题',
  `category` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '类型,1:文章;2:单页',
  `source_type` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '内容来源;1:模板页面;2:文本编辑器',
  `content` longtext COMMENT '文章内容',
  `type` int(11) NOT NULL COMMENT '博文分类',
  `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发表者用户id',
  `is_published` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态;1:已发布;0:未发布;',
  `is_allow_comments` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '评论状态;1:允许;0:不允许',
  `is_top` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶;1:置顶;0:不置顶',
  `Viewing_times` int(20) unsigned NOT NULL DEFAULT '0' COMMENT '查看数',
  `favorites` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数',
  `comment_count` int(20) unsigned NOT NULL DEFAULT '0' COMMENT '评论数',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `published_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `keywords` varchar(150) NOT NULL DEFAULT '' COMMENT 'seo keywords',
  `abstruct` varchar(500) NOT NULL DEFAULT '' COMMENT '摘要',
  `source` varchar(150) NOT NULL DEFAULT '' COMMENT '转载文章的来源',
  `thumbnail` varchar(100) NOT NULL DEFAULT '' COMMENT '缩略图',
  `more` text COMMENT '扩展属性,如缩略图;格式为json',
  `time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `del_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '删除时间',
  `status` tinyint(4) NOT NULL COMMENT '0:禁用, 1:正常, 2:已删除',
  PRIMARY KEY (`id`),
  KEY `type_status_date` (`category`,`is_published`,`time`,`id`),
  KEY `user_id` (`uid`),
  KEY `create_time` (`time`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8 COMMENT='文章表';
INSERT INTO `ithink_blog_article` (`id` , `title` , `category` , `source_type` , `content` , `type` , `uid` , `is_published` , `is_allow_comments` , `is_top` , `Viewing_times` , `favorites` , `comment_count` , `update_time` , `published_time` , `keywords` , `abstruct` , `source` , `thumbnail` , `more` , `time` , `del_time` , `status`) VALUES ( 14,'iThink开发框架',1,1,'&amp;lt;h1 class=\\&amp;quot;align-center\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; -webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; text-size-adjust: none; font-size: 2.25em; margin: 0px 0px 14px; font-family: dashicons, &amp;amp;quot;Segoe UI&amp;amp;quot;, &amp;amp;quot;WenQuanYi Micro Hei&amp;amp;quot;, &amp;amp;quot;WenQuanYi Micro Hei Mono&amp;amp;quot;, &amp;amp;quot;Microsoft Yahei&amp;amp;quot;, &amp;amp;quot;Microsoft Yahei Mono&amp;amp;quot;, 微软雅黑, sans-serif; padding: 0px; font-weight: 400; line-height: 1.2; min-height: 1rem; text-align: center; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);\\&amp;quot;&amp;gt;iThink web开发框架&amp;lt;/h1&amp;gt;&amp;lt;hr/&amp;gt;&amp;lt;h1 style=\\&amp;quot;box-sizing: inherit; -webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; text-size-adjust: none; font-size: 2.25em; margin: 14px 0px; font-family: dashicons, &amp;amp;quot;Segoe UI&amp;amp;quot;, &amp;amp;quot;WenQuanYi Micro Hei&amp;amp;quot;, &amp;amp;quot;WenQuanYi Micro Hei Mono&amp;amp;quot;, &amp;amp;quot;Microsoft Yahei&amp;amp;quot;, &amp;amp;quot;Microsoft Yahei Mono&amp;amp;quot;, 微软雅黑, sans-serif; padding: 0px; font-weight: 400; line-height: 1.2; min-height: 1rem; color: rgb(82, 82, 82); white-space: normal; background-color: rgb(255, 255, 255);\\&amp;quot;&amp;gt;&amp;lt;a style=\\&amp;quot;box-sizing: inherit; -webkit-font-smoothing: antialiased; -webkit-tap-highlight-color: transparent; text-size-adjust: none; background-color: transparent; color: rgb(65, 131, 196); margin-top: -10px; position: 