DROP TABLE IF EXISTS `ithinkphp_blog_article`;
CREATE TABLE `ithinkphp_blog_article` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `title` char(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT '标题',
  `category` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '类型,1:文章;2:单页',
  `source_type` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '内容来源;1:模板页面;2:文本编辑器',
  `content` longtext COMMENT '文章内容',
  `type` int(11) NOT NULL COMMENT '博文分类',
  `uid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发表者用户id',
  `is_published` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态;1:已发布;0:未发布;',
  `is_allow_comments` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '评论状态;1:允许;0:不允许',
  `is_top` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶;1:置顶;0:不置顶',
  `Viewing_times` int(20) unsigned NOT NULL DEFAULT '0' COMMENT '查看数',
  `favorites` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数',
  `comment_count` int(20) unsigned NOT NULL DEFAULT '0' COMMENT '评论数',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `published_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `keywords` varchar(150) NOT NULL DEFAULT '' COMMENT 'seo keywords',
  `abstruct` varchar(500) NOT NULL DEFAULT '' COMMENT '摘要',
  `source` varchar(150) NOT NULL DEFAULT '' COMMENT '转载文章的来源',
  `thumbnail` varchar(100) NOT NULL DEFAULT '' COMMENT '缩略图',
  `more` text COMMENT '扩展属性,如缩略图;格式为json',
  `time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',
  `del_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '删除时间',
  `status` tinyint(4) NOT NULL COMMENT '0:禁用, 1:正常, 2:已删除',
  PRIMARY KEY (`id`),
  KEY `type_status_date` (`category`,`is_published`,`time`,`id`),
  KEY `user_id` (`uid`),
  KEY `create_time` (`time`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8 COMMENT='文章表';
INSERT INTO `ithinkphp_blog_article` (`id` , `title` , `category` , `source_type` , `content` , `type` , `uid` , `is_published` , `is_allow_comments` , `is_top` , `Viewing_times` , `favorites` , `comment_count` , `update_time` , `published_time` , `keywords` , `abstruct` , `source` , `thumbnail` , `more` , `time` , `del_time` , `status`) VALUES ( 14,'iThink开发框架',1,1,'&amp;lt;h1 class=\\&amp;quot;align-center\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; font-size: 2.25em; margin: 0px 0px 14px; padding: 0px; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-weight: bolder; font-size: 16px;\\&amp;quot;&amp;gt;应用化&amp;lt;/span&amp;gt;&amp;lt;br/&amp;gt;&amp;lt;/h1&amp;gt;&amp;lt;blockquote class=\\&amp;quot;danger\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;h4 style=\\&amp;quot;box-sizing: inherit; padding: 0px; line-height: 1.2; margin: 0px; font-weight: 400; font-size: 1.1em;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;像开发 安卓 / IOS 应用一样的开发web应用，iThink 提供完善的安装，卸载，打包机制，所有开发的应用都以一个应用包的形式存在，可以打包后免费或收费的分享他人使用&amp;lt;/span&amp;gt;&amp;lt;/h4&amp;gt;&amp;lt;/blockquote&amp;gt;&amp;lt;h3 style=\\&amp;quot;box-sizing: inherit; padding: 0px; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; font-weight: bolder; font-family: 微软雅黑, \\&amp;quot;&amp;gt;自动化&amp;lt;/span&amp;gt;&amp;lt;/h3&amp;gt;&amp;lt;blockquote class=\\&amp;quot;danger\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;h3 style=\\&amp;quot;box-sizing: inherit; padding: 0px; line-height: 1.43; margin: 0px 0px 14px; font-weight: 400; font-size: 1.2em;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;一键生成web应用骨架代码和数据表的 CRUD,包括控制器口，模型，验证器等基类，实现高速开发&amp;lt;/span&amp;gt;&amp;lt;/h3&amp;gt;&amp;lt;h4 style=\\&amp;quot;box-sizing: inherit; padding: 0px; line-height: 1.2; margin: 14px 0px 0px; font-weight: 400; font-size: 1.1em;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;自动构造表格，表单元素，无需修改前端页面&amp;lt;/span&amp;gt;&amp;lt;/h4&amp;gt;&amp;lt;/blockquote&amp;gt;&amp;lt;h3 style=\\&amp;quot;box-sizing: inherit; padding: 0px; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; font-weight: bolder; font-family: 微软雅黑, \\&amp;quot;&amp;gt;抽象化&amp;lt;/span&amp;gt;&amp;lt;/h3&amp;gt;&amp;lt;blockquote class=\\&amp;quot;danger\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;h4 style=\\&amp;quot;box-sizing: inherit; padding: 0px; line-height: 1.2; margin: 0px; font-weight: 400; font-size: 1.1em;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;iThink 分controller、logic、model、service、view 共5层，层层抽象，层层封装，最大限度的实现代码复用，无需编写一行重复代码&amp;lt;/span&amp;gt;&amp;lt;/h4&amp;gt;&amp;lt;/blockquote&amp;gt;&amp;lt;h3 style=\\&amp;quot;box-sizing: inherit; padding: 0px; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; font-weight: bolder; font-family: 微软雅黑, \\&amp;quot;&amp;gt;权限精细化&amp;lt;/span&amp;gt;&amp;lt;/h3&amp;gt;&amp;lt;blockquote class=\\&amp;quot;danger\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;h4 style=\\&amp;quot;box-sizing: inherit; padding: 0px; line-height: 1.2; margin: 0px; font-weight: 400; font-size: 1.1em;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;基于 RBAC + Auth 的权限管理机制，精确到按钮的控制，每个按钮有 is_display 属性，配合 Auth 类可灵活控制页面上每个按钮的显示与否&amp;lt;/span&amp;gt;&amp;lt;/h4&amp;gt;&amp;lt;/blockquote&amp;gt;&amp;lt;h1 style=\\&amp;quot;box-sizing: inherit; font-size: 2.25em; margin: 14px 0px; padding: 0px; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;体验demo&amp;amp;nbsp;&amp;lt;/span&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;http://demo.ithinkphp.org/admin\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;demo.ithinkphp.org/admin&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/h1&amp;gt;&amp;lt;hr/&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;iThink是一个基于thinkphp5.0开发的模块化，应用化，自动构造化的WEB应用开发框架（Web Application Framework），集成高可用，高稳定的页面构造器，可以极其方便的构造后台页面包括表单，表格，上传等组件在内的各种元素！是笔者在参考了当前10多个主流的基于TP5的CMF/CMS的架构和思想后的沉淀出的产品！&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;iThink实现应用与框架核心分离，可以将开发的应用打包，以免费或者收费的形式分享其他人使用。&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;服务器端CURD操作不需要修改前端html页面，前端页面所有元素都通过php构造完成，由笔者倾心打造的页面构造器，只为省去修改前端页面的烦恼。&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;页面所有元素都可定制，极大提高开发效率，10分钟即可完成一个表的curd功能，十分高效。&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;服务器端架构遵循十分先进的设计思想，灵活应用各种设计模式，分controller、logic、model、service、view 共5层，拥有一套自定义的依赖注入机制，将&amp;ldquo;抽象&amp;rdquo;的思想发挥的淋漓尽致，最大限度的实现代码复用，让你不需要多写一行重复代码，将全部精力投入编写应用逻辑！&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;table class=\\&amp;quot;table\\&amp;quot; width=\\&amp;quot;1265\\&amp;quot;&amp;gt;&amp;lt;tbody style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot; class=\\&amp;quot;firstRow\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;产品名称&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;iThink&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;官方网站&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;http://www.ithinkphp.org/\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;www.ithinkphp.org&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;后台演示&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;http://demo.ithinkphp.org/admin\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;demo.ithinkphp.org/admin&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;（输入验证码即可登录）&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;前台演示&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;http://demo.ithinkphp.org/\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;demo.ithinkphp.org&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;码云仓库&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;https://gitee.com/wf5858585858/iThink\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;https://gitee.com/wf5858585858/iThink&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;交流社区&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;http://forum.ithinkphp.org/\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;forum.ithinkphp.org&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;开发手册（持续更新中）&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;a target=\\&amp;quot;_blank\\&amp;quot; href=\\&amp;quot;https://www.kancloud.cn/wf00568/main\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;https://www.kancloud.cn/wf00568/main&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;交流QQ群&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;419395011&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;tr style=\\&amp;quot;box-sizing: inherit;\\&amp;quot;&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;联系邮箱&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;td style=\\&amp;quot;box-sizing: inherit; border-color: rgb(224, 224, 224); padding: 3px 12px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;wf585858@yeah.net&amp;lt;/span&amp;gt;&amp;lt;/td&amp;gt;&amp;lt;/tr&amp;gt;&amp;lt;/tbody&amp;gt;&amp;lt;/table&amp;gt;&amp;lt;h3 class=\\&amp;quot;align-center\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; padding: 0px; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; font-weight: bolder; font-family: 微软雅黑, \\&amp;quot;&amp;gt;特性&amp;lt;/span&amp;gt;&amp;lt;/h3&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;高效开发-表单与表格元素构造器&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;由笔者倾心打造的页面构造器类库，所有的常用的页面元素，可以通过调用一个函数来直接生成，包括表单里常用的输入组件（文本，密码，单选，复选，开关，option，时间选择，单/多文件上传，单/多图片上传，地域选择，富文本编辑器（自带ueditor和summernote））等等，和表格里的常用组件，包括单个td，tr，按钮，开关，option选项等等，还有搜索表单框的各种组件等，有需要亦可自己添加组件，详情可参考文档 ，代码参考 extend\\\\builder;(其中integrationTags类是元素生成类，是对其他功能的封装，可直接调用，使用方式参考admin模块下的view文件夹)，下面有张图可以比较直观的看到效果&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;BRAC+AUTH 权限验证 (重写 AUTH 类)&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;基于RBAC和Auth类结合的权限验证管理系统，实现标准的BRAC0授权模型，如果有需要可升级BRAC1-3，参考&amp;amp;nbsp;&amp;lt;/span&amp;gt;&amp;lt;a href=\\&amp;quot;http://www.woshipm.com/pd/440765.html\\&amp;quot; target=\\&amp;quot;_blank\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;http://www.woshipm.com/pd/440765.html&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;对TP自带的Auth类做了全面的抽象和重写，功能更细致，更完善，与系统彻底解耦（可以把类提出来，放到YII或者laravel里用都没问题），参考&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;auth\\\\permission\\\\Auth;&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;元素级权限控制机制&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;可以对页面里的单个元素（一个按钮，表格里的一个字段等）做精细化控制，是否可见，是否可直接编辑。比如设置某表格，在A角色登陆时，当B字段的值为5的时候，C字段是否可编辑，删除按钮是否可用等，十分灵活，只需要编写配置文件即可 ，参考&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;auth\\\\view\\\\Auth;&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;amp;nbsp;配置文件参考&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;app\\\\extra&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;分片上传处理&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;重写上传处理逻辑，TP官方自带的上传类使用方便，但因为不是分片上传（参考think\\\\File类里的move方法使用的move_uploaded_file函数），这意味着如果需要上传G级文件，将导致性能问题（几乎是不可能完成）。为此重写了上传处理逻辑，配合webuploader插件（百度官方插件），实现分片上传，上传G级文件也没问题，前后端组件都已经封装，只需调用一个函数即可,包括单/多文件上传，单/多图片上传，前端调用参考&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;extend\\\\builder\\\\integrationTags&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;的&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;upload&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;方法，分片处理逻辑参考&amp;amp;nbsp;&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;extend\\\\upload\\\\uploader&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;应用化&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;平台本身只有后台管理，其他功能需要安装应用来实现，拥有十分完善的安装，卸载，应用打包，数据配置自动生成机制。基本可以理解为安卓系统和apk安装包之间的关系，平台安装后自带有一个博客系统供开发者参考学习 ，参考&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;app\\\\admin\\\\logic\\\\Module;&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;自定义依赖注入机制&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;拥有一套自定义的依赖注入机制，trait引入，通过__get实现，可以在任何类里仅通过调用自身属性即可得到任何模块下的model、logic、service的实例，无需繁琐的使用model方法实例化模型，参考&amp;amp;nbsp;&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;app\\\\common\\\\common\\\\set;&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;齐全的调用示例&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;对笔者提及到的每个功能，都写的专门的调用示例，参考&amp;amp;nbsp;&amp;lt;/span&amp;gt;&amp;lt;code style=\\&amp;quot;box-sizing: inherit; font-family: Consolas, Monaco, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;app\\\\demo\\\\controller\\\\Example&amp;lt;/span&amp;gt;&amp;lt;/code&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;架构&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;基于TP5编写则意味着TP5官网声明的功能都可以随意搭配，包括分布式、负载均衡、集群、Redis、分库分表、nosql缓存等各种机制，参考TP5官方文档&amp;lt;/span&amp;gt;&amp;lt;a href=\\&amp;quot;https://www.kancloud.cn/manual/thinkphp5/122950\\&amp;quot; target=\\&amp;quot;_blank\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; background-color: transparent; color: rgb(65, 131, 196); text-decoration: underline; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;https://www.kancloud.cn/manual/thinkphp5/122950&amp;lt;/span&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px; font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;p style=\\&amp;quot;box-sizing: inherit; margin-top: 0px; margin-bottom: 0px; line-height: 2; padding: 0px;\\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;iThink 在TP的所提倡的mvc的基础上，做了更细化的分层，抽象出了logic和service层，可以更好的实现代码的复用，每个层级都有自己的职责，互不干扰&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;规范&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;遵循PSR-2、PSR-4规范，Composer及单元测试支持&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;拥有自己的数据库定义规范，确保每个开发者开都有相同的开发规范&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;开源&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;程序完全开源不加密，采用 apache2 协议发布，这意味您可以自由修改、派生版本、商用而不用担心任何法律风险。文档十分齐全，代码中预留了大量钩子和注释，方便二次开发，最大化满足用户的功能需求。需要声明的是您二次开发的作品使用和运营过程必须遵守国家相关法律法规，iThink官方不承担任何责任。&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;严谨&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;（by thinkphp）异常严谨的错误检测和安全机制，详细的日志信息，为你的开发保驾护航；&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;灵活&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;（by thinkphp）减少核心依赖，扩展更灵活、方便，支持命令行指令扩展；&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;h2 style=\\&amp;quot;box-sizing: inherit; padding: 0px 0px 0.3em; font-family: dashicons, \\&amp;quot;&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;API友好&amp;lt;/span&amp;gt;&amp;lt;/h2&amp;gt;&amp;lt;ul class=\\&amp;quot;task-list list-paddingleft-2\\&amp;quot; style=\\&amp;quot;list-style-type: none;\\&amp;quot;&amp;gt;&amp;lt;li&amp;gt;&amp;lt;p&amp;gt;&amp;lt;span style=\\&amp;quot;font-family: 微软雅黑, \\&amp;quot;&amp;gt;&amp;lt;span class=\\&amp;quot;icon square\\&amp;quot; style=\\&amp;quot;box-sizing: inherit; display: inline-block; opacity: 1; margin: 0px 0.25rem 0px 0px; width: 1.18em; height: 1em; font-family: Icons; text-decoration: inherit; text-align: center; speak: none; -webkit-font-smoothing: antialiased; backface-visibility: hidden; font-size: 1em; position: absolute; left: -24px; top: 1px;\\&amp;quot;&amp;gt;&amp;lt;/span&amp;gt;（by thinkphp）出色的性能和REST支持、远程调试，更好的支持API开发；&amp;lt;/span&amp;gt;&